"use strict";function removeMovieFromDatabase(t){var n=t.firstElementChild.innerHTML;try{fetch("/api/movies/".concat(n),{method:"DELETE"}).then(function(n){n.json().then(function(n){n.message?Swal.fire({icon:"error",title:"Oops...",html:n.message}):(t.classList.add("fall"),t.ontransitionend=function(n){return t.remove()},Swal.fire({icon:"success",title:"Thành công",html:"Xóa phim thành công"}))})})}catch(t){return!1}}function removeMovie(t){var n=t.target,e=n.parentElement;"trash-btn"===n.classList[0]?Swal.fire({title:"Cảnh báo",icon:"warning",html:"Bạn có chắc chắn xóa phim <b>".concat(e.innerText,"</b> không?"),confirmButtonColor:"#E74C3C",confirmButtonText:"Chắc chắn",showCancelButton:!0,cancelButtonColor:"#99A3A4",cancelButtonText:"Không"}).then(function(t){t.isConfirmed&&removeMovieFromDatabase(e)}):"edit-btn"===n.classList[0]?e.classList.toggle("completed"):"complete-btn"===n.classList[0]&&e.classList.toggle("completed")}