"use strict";function removeMovieFromDatabase(e){var t=e.firstElementChild.innerHTML;try{fetch("/api/movies/".concat(t),{method:"DELETE"}).then(function(t){t.json().then(function(t){t.message?alert(t.message):(e.classList.add("fall"),e.ontransitionend=function(t){return e.remove()})})})}catch(e){return!1}}function removeMovie(e){var t=e.target,n=t.parentElement;"trash-btn"===t.classList[0]?confirm("Bạn có chắc chắn xóa phim này không?")&&removeMovieFromDatabase(n):"edit-btn"===t.classList[0]?n.classList.toggle("completed"):"complete-btn"===t.classList[0]&&n.classList.toggle("completed")}