"use strict";var theaterRowElements=document.querySelectorAll("#mntheater-table tbody tr"),tableRowElements=document.querySelectorAll("#mntheater-table tr"),tableBodyElement=document.querySelector("#mntheater-table tbody"),displayCheckboxElements=document.querySelectorAll("#mntheater-display .mmntheater-display-item input"),COL=Object.freeze({NAME:1,CITY:2,ADDRESS:3,PHONE:4,ROOMS:5,ID:6});function showTableColumn(e){tableRowElements.forEach(function(t){t.children[e].style.display="table-cell"})}function hideTableColumn(e){tableRowElements.forEach(function(t){t.children[e].style.display="none"})}function refreshDisplay(){displayCheckboxElements.forEach(function(e,t){!1===e.checked&&hideTableColumn(t+1)})}function eventDisplayCheckboxes(){displayCheckboxElements.forEach(function(e,t){e.addEventListener("click",function(){!0===e.checked?showTableColumn(t+1):hideTableColumn(t+1)})})}function eventRowTheaters(){theaterRowElements.forEach(function(e){e.addEventListener("click",function(){var t=e.children[COL.ID].innerHTML;fetch("/api/theaters/".concat(t)).then(function(e){e.json().then(function(e){$("#myModal").modal("show"),console.log(e.name)})})})})}function handleEvents(){eventDisplayCheckboxes(),eventRowTheaters()}function main(){refreshDisplay(),handleEvents()}main();