"use strict";var userRowElements=document.querySelectorAll("#mnuser-table tbody tr"),tableRowElements=document.querySelectorAll("#mnuser-table tr"),tableBodyElement=document.querySelector("#mnuser-table tbody"),displayCheckboxElements=document.querySelectorAll("#mnuser-display .mmnuser-display-item input");function showTableColumn(e){tableRowElements.forEach(function(n){n.children[e].style.display="table-cell"})}function hideTableColumn(e){tableRowElements.forEach(function(n){n.children[e].style.display="none"})}function refreshDisplay(){displayCheckboxElements.forEach(function(e,n){!1===e.checked&&hideTableColumn(n)})}function eventDisplayCheckboxes(){displayCheckboxElements.forEach(function(e,n){e.addEventListener("click",function(){!0===e.checked?showTableColumn(n):hideTableColumn(n)})})}function handleEvents(){eventDisplayCheckboxes()}function main(){refreshDisplay(),handleEvents()}main();