"use strict";var theaterRowElements=document.querySelectorAll("#mntheater-table tbody tr"),tableRowElements=document.querySelectorAll("#mntheater-table tr"),tableBodyElement=document.querySelector("#mntheater-table tbody"),displayCheckboxElements=document.querySelectorAll("#mntheater-display .mmntheater-display-item input");function showTableColumn(e){tableRowElements.forEach(function(t){t.children[e].style.display="table-cell"})}function hideTableColumn(e){tableRowElements.forEach(function(t){t.children[e].style.display="none"})}function refreshDisplay(){displayCheckboxElements.forEach(function(e,t){!1===e.checked&&hideTableColumn(t)})}function eventDisplayCheckboxes(){displayCheckboxElements.forEach(function(e,t){e.addEventListener("click",function(){!0===e.checked?showTableColumn(t+1):hideTableColumn(t+1)})})}function handleEvents(){eventDisplayCheckboxes()}function main(){refreshDisplay(),handleEvents()}main();