"use strict";var theaterRowElements=document.querySelectorAll("#mntheater-table tbody tr"),tableRowElements=document.querySelectorAll("#mntheater-table tr"),tableBodyElement=document.querySelector("#mntheater-table tbody"),displayCheckboxElements=document.querySelectorAll("#mntheater-display .mmntheater-display-item input"),COL=Object.freeze({NAME:1,CITY:2,ADDRESS:3,PHONE:4,ROOMS:5,ID:6});function showTableColumn(e){tableRowElements.forEach(function(n){n.children[e].style.display="table-cell"})}function hideTableColumn(e){tableRowElements.forEach(function(n){n.children[e].style.display="none"})}function refreshDisplay(){displayCheckboxElements.forEach(function(e,n){!1===e.checked&&hideTableColumn(n+1)})}function openModal(){$("#myModal").css("display","block")}function closeModal(){$("#myModal").css("display","none"),console.log("helo")}function eventDisplayCheckboxes(){displayCheckboxElements.forEach(function(e,n){e.addEventListener("click",function(){!0===e.checked?showTableColumn(n+1):hideTableColumn(n+1)})})}function eventRowTheaters(){theaterRowElements.forEach(function(e){e.addEventListener("click",function(){var n=e.children[COL.ID].innerHTML;fetch("/api/theaters/".concat(n)).then(function(e){e.json().then(function(e){$("h2").html(e.name),$(".nameField").html(e.name),$(".cityField").html(e.city),$(".addressField").html(e.address),$(".phoneField").html(e.phone),$(".roomField").html(e.rooms[0]);for(var n=1;n<e.rooms.length;n+=1)$(".roomField").append(", ".concat(e.rooms[n]));openModal()})})})})}function handleEvents(){eventDisplayCheckboxes(),eventRowTheaters()}function main(){refreshDisplay(),handleEvents()}main();