"use strict";var ticketRowElements=document.querySelectorAll("#mnticket-table tbody tr"),tableRowElements=document.querySelectorAll("#mnticket-table tr"),tableBodyElement=document.querySelector("#mnticket-table tbody"),displayCheckboxElements=document.querySelectorAll("#mnticket-display .mmnticket-display-item input");function showTableColumn(e){tableRowElements.forEach(function(t){t.children[e].style.display="table-cell"})}function hideTableColumn(e){tableRowElements.forEach(function(t){t.children[e].style.display="none"})}function refreshDisplay(){displayCheckboxElements.forEach(function(e,t){!1===e.checked&&hideTableColumn(t+1)})}function eventDisplayCheckboxes(){displayCheckboxElements.forEach(function(e,t){e.addEventListener("click",function(){!0===e.checked?showTableColumn(t+1):hideTableColumn(t+1)})})}function handleEvents(){eventDisplayCheckboxes()}function main(){refreshDisplay(),handleEvents()}main();